  _buttonPressed(ToolBarButton toolBarButton) {
    switch (toolBarButton.buttonType) {
      case ButtonType.BOLD:
        var isSelect = toolBarButton.isSelect;
        _controller.evaluateJavascript('setBold(${!isSelect})');
        setState(() {
          toolBarButton.isSelect = !isSelect;
        });
        break;
      case ButtonType.CODE_VIEW:
        var isSelect = toolBarButton.isSelect;
        _controller.evaluateJavascript('setCodeblock(${!isSelect})');
        setState(() {
          toolBarButton.isSelect = !isSelect;
        });
        break;
      case ButtonType.LINK:
        var isSelect = toolBarButton.isSelect;
        _controller.evaluateJavascript('setLink(${!isSelect})');
        setState(() {
          toolBarButton.isSelect = !isSelect;
        });
        break;
      case ButtonType.LIST_BULLET:
        var isSelect = toolBarButton.isSelect;
        _controller.evaluateJavascript('setList(${!isSelect})');
        setState(() {
          toolBarButton.isSelect = !isSelect;
        });
        break;
      case ButtonType.List_ORDERED:
        var isSelect = toolBarButton.isSelect;
        if (!isSelect) {
          _controller.evaluateJavascript("setList('ordered')");
        } else {
          _controller.evaluateJavascript('setList(${!isSelect})');
        }
        setState(() {
          toolBarButton.isSelect = !isSelect;
        });
        break;
      case ButtonType.IMAGE:
        _controller.evaluateJavascript("insertImage('$base64')");
        break;
      case ButtonType.BLOCK_QUOTE:
        var isSelect = toolBarButton.isSelect;
        _controller.evaluateJavascript('setBlockquote(${!isSelect})');
        setState(() {
          toolBarButton.isSelect = !isSelect;
        });
        break;
      case ButtonType.ITALIC:
        var isSelect = toolBarButton.isSelect;
        _controller.evaluateJavascript('setItalic(${!isSelect})');
        setState(() {
          toolBarButton.isSelect = !isSelect;
        });
        break;
      case ButtonType.UNDER_LINE:
        var isSelect = toolBarButton.isSelect;
        _controller.evaluateJavascript('setUnderline(${!isSelect})');
        setState(() {
          toolBarButton.isSelect = !isSelect;
        });
        break;
      case ButtonType.STRIKE_THROUGH:
        var isSelect = toolBarButton.isSelect;
        _controller.evaluateJavascript('setStrike(${!isSelect})');
        setState(() {
          toolBarButton.isSelect = !isSelect;
        });
        break;
    }
  }

  JavascriptChannel getTextJavascriptChannel(BuildContext context) {
    return JavascriptChannel(
        name: 'GetEditorChange',
        onMessageReceived: (JavascriptMessage message) async {
          List<ButtonType> types = [];
          var quillFormat = await _controller.evaluateJavascript('quill.getFormat()');
//          print(quillFormat);
          var bold = quillFormat.indexOf('bold');
          if (bold != -1) types.add(ButtonType.BOLD);
          var codeBlock = quillFormat.indexOf('code-block');
          if (codeBlock != -1) types.add(ButtonType.CODE_VIEW);
          var blockQuote = quillFormat.indexOf('blockquote');
          if (blockQuote != -1) types.add(ButtonType.BLOCK_QUOTE);
          var bullet = quillFormat.indexOf('list = bullet');
          if (bullet != -1) types.add(ButtonType.LIST_BULLET);
          var listOrder = quillFormat.indexOf('list = ordered');
          if (listOrder != -1) types.add(ButtonType.List_ORDERED);
          var italic = quillFormat.indexOf('italic');
          if (italic != -1) types.add(ButtonType.ITALIC);
          var underLine = quillFormat.indexOf('underline');
          if (underLine != -1) types.add(ButtonType.UNDER_LINE);
          var strike = quillFormat.indexOf('strike');
          if (strike != -1) types.add(ButtonType.STRIKE_THROUGH);
          eventBus.fire(ButtonChangeEvent(types));
        });
  }

